mapper.select:
select product_number, product_id, product_category_1, product_category_2, product_subject, product_content, product_img_1, product_img_2, product_img_3, product_img_4, product_img_5, product_starting_price, product_bidding_unit, to_char(product_end_date, 'yyyy-mm-dd hh24:mi') as product_end_date, product_delivery, product_transaction_area, product_purchase_price, product_re_auction, product_current_price, product_bids, product_participants, product_credit_score, product_views, to_char(product_issue_date, 'yyyy-mm-dd hh24:mi') as product_issue_date, product_progress from product where product_progress=0 and product_number in
#{item}
order by product_end_date desc

select max(bid_price) from bid_list where bid_id=#{userId} and bid_product_number in
#{item}
group by bid_product_number
